/* BANNER.insertBannerInfo */
INSERT INTO BANNER_INFO ( TITLE , CONTENT , LINK_ADDRESS , IS_NEW_WINDOW , AREA_CODE , TYPE1 , ORDER_LEVEL , START_SHOW_DATE , END_SHOW_DATE , REAL_FILE_NAME , SAVE_FILE_NAME , FILE_PATH , STATUS , REG_DATE , MOD_DATE ) VALUES ( #title# , #content# , #link_address# , #is_new_window# , #area_code# , #type1# , #order_level# , #start_show_date# , #end_show_date# , #real_file_name# , #save_file_name# , #file_path# , #status# , NOW() , NOW() )
SELECT @@IDENTITY AS BANNER_KEY
