sqlMap.select:
/* COMPANY.getCompanyInfo */

SELECT , CONTENT FROM PARTI_COMPANY
WHERE COMPANY_KEY = #company_key#

	/* COMPANY.getCompanyTotalCount */        
    
    SELECT COUNT(*) AS TOTAL_COUNT FROM PARTI_COMPANY
    WHERE 1=1

/* COMPANY.getCompanyList */
SELECT FROM PARTI_COMPANY WHERE 1=1
ORDER BY COMPANY_NAME LIMIT #recordRowNum# , #pageSize#
