sqlMap.insert:
/* MUSEUM.insertMuseumInfo */
INSERT INTO MUSEUM_INFO ( KIND , MUSEUM_NAME , CONT_EXPL , CLOSED_DAY , PHONE , FAX , ZIP_CODE , ADDRESS1 , ADDRESS2 , MAIN_IMAGE , SUB_IMAGE , LINK_DOMAIN , STATUS , REG_DATE , MOD_DATE ) VALUES ( #kind# , #museum_name# , #cont_expl# , #closed_day# , #phone# , #fax# , #zip_code# , #address1# , #address2# , #main_image# , #sub_image# , #link_domain# , #status# , NOW() , NOW() )
SELECT @@IDENTITY AS MUSEUM_KEY

sqlMap.select:
/* MUSEUM.getMuseumMenuList */

SELECT MENU_CODE, MENU_NAME, KIND FROM MUSEUM_MENU_INFO
ORDER BY ORDER_LEVEL

/* MUSEUM.getMuseumMenuContList */
SELECT A.*, B.MENU_NAME FROM ( SELECT MUSEUM_NO , MENU_CODE , BOARD_ID FROM MUSEUM_MENU_CONTENT WHERE 1=1
MUSEUM_NO = #museum_no#
) AS A, MUSEUM_MENU_INFO AS B WHERE A.MENU_CODE = B.MENU_CODE ORDER BY B.ORDER_LEVEL
