sqlMap.insert:
/* POPUP.insertPopupInfo */
INSERT INTO POPUP_INFO ( TITLE , KIND , LINK_ADDRESS , IS_NEW_WINDOW , WIDTH_SIZE , HEIGHT_SIZE , TOP_MARGIN , LEFT_MARGIN , START_SHOW_DATE , END_SHOW_DATE , REAL_FILE_NAME , SAVE_FILE_NAME , FILE_PATH , STATUS , REG_DATE , MOD_DATE ) VALUES ( #title# , #kind# , #link_address# , #is_new_window# , #width_size# , #height_size# , #top_margin# , #left_margin# , #start_show_date# , #end_show_date# , #real_file_name# , #save_file_name# , #file_path# , #status# , NOW() , NOW() )
SELECT @@IDENTITY AS POPUP_KEY

sqlMap.select:
/* POPUP.getPopupTotalCount */

SELECT COUNT(*) AS TOTAL_COUNT FROM POPUP_INFO
WHERE STATUS IN ('Y','N')
